import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import { Switch } from "@/components/ui/switch";
import { useToast } from "@/hooks/use-toast";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertBlogPostSchema, type BlogPost, type InsertBlogPost } from "@shared/schema";
import { Plus, Pencil, Trash2, Calendar, Loader2 } from "lucide-react";
import { queryClient, apiRequest } from "@/lib/queryClient";

export default function AdminBlog() {
  const { toast } = useToast();
  const [dialogOpen, setDialogOpen] = useState(false);
  const [editingPost, setEditingPost] = useState<BlogPost | null>(null);

  const { data: blogPosts, isLoading } = useQuery<BlogPost[]>({
    queryKey: ["/api/blog-posts"],
    queryFn: async () => {
      const response = await fetch("/api/blog-posts");
      if (!response.ok) throw new Error("Failed to fetch blog posts");
      return response.json();
    },
  });

  const form = useForm<InsertBlogPost>({
    resolver: zodResolver(insertBlogPostSchema),
    defaultValues: {
      title: "",
      slug: "",
      excerpt: "",
      content: "",
      coverImage: "",
      tags: [],
      published: "false",
      publishedAt: null,
    },
  });

  const createMutation = useMutation({
    mutationFn: async (data: InsertBlogPost) => {
      return await apiRequest("POST", "/api/blog-posts", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/blog-posts"] });
      toast({ title: "Blog post created successfully" });
      setDialogOpen(false);
      form.reset();
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: InsertBlogPost }) => {
      return await apiRequest("PATCH", `/api/blog-posts/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/blog-posts"] });
      toast({ title: "Blog post updated successfully" });
      setDialogOpen(false);
      setEditingPost(null);
      form.reset();
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("DELETE", `/api/blog-posts/${id}`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/blog-posts"] });
      toast({ title: "Blog post deleted successfully" });
    },
  });

  const handleEdit = (post: BlogPost) => {
    setEditingPost(post);
    form.reset({
      title: post.title,
      slug: post.slug,
      excerpt: post.excerpt,
      content: post.content,
      coverImage: post.coverImage || "",
      tags: post.tags,
      published: post.published,
      publishedAt: post.publishedAt,
    });
    setDialogOpen(true);
  };

  const onSubmit = (data: InsertBlogPost) => {
    const formData = {
      ...data,
      coverImage: data.coverImage || null,
      publishedAt: data.published === 'true' && !data.publishedAt ? new Date() : data.publishedAt,
    };

    if (editingPost) {
      updateMutation.mutate({ id: editingPost.id, data: formData });
    } else {
      createMutation.mutate(formData);
    }
  };

  const generateSlug = (title: string) => {
    return title
      .toLowerCase()
      .replace(/[^a-z0-9]+/g, '-')
      .replace(/(^-|-$)/g, '');
  };

  const handleTitleBlur = () => {
    const title = form.getValues('title');
    const currentSlug = form.getValues('slug');
    if (title && !currentSlug) {
      form.setValue('slug', generateSlug(title));
    }
  };

  const sortedPosts = blogPosts?.sort(
    (a, b) => new Date(b.updatedAt).getTime() - new Date(a.updatedAt).getTime()
  );

  return (
    <div className="min-h-screen py-20">
      <div className="container mx-auto px-4 lg:px-8 max-w-7xl">
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold">Manage Blog Posts</h1>
            <p className="text-muted-foreground">Create and manage your blog content</p>
          </div>
          <Dialog open={dialogOpen} onOpenChange={setDialogOpen}>
            <DialogTrigger asChild>
              <Button
                onClick={() => {
                  setEditingPost(null);
                  form.reset();
                }}
              >
                <Plus className="mr-2 h-4 w-4" />
                New Post
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-3xl max-h-[90vh] overflow-y-auto">
              <DialogHeader>
                <DialogTitle>
                  {editingPost ? "Edit Blog Post" : "Create Blog Post"}
                </DialogTitle>
              </DialogHeader>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <div>
                  <Label htmlFor="title">Title</Label>
                  <Input
                    id="title"
                    {...form.register("title")}
                    onBlur={handleTitleBlur}
                    placeholder="Enter post title"
                  />
                  {form.formState.errors.title && (
                    <p className="text-sm text-red-500 mt-1">
                      {form.formState.errors.title.message}
                    </p>
                  )}
                </div>

                <div>
                  <Label htmlFor="slug">Slug (URL)</Label>
                  <Input
                    id="slug"
                    {...form.register("slug")}
                    placeholder="post-url-slug"
                  />
                  {form.formState.errors.slug && (
                    <p className="text-sm text-red-500 mt-1">
                      {form.formState.errors.slug.message}
                    </p>
                  )}
                </div>

                <div>
                  <Label htmlFor="excerpt">Excerpt</Label>
                  <Textarea
                    id="excerpt"
                    {...form.register("excerpt")}
                    placeholder="Brief description of the post"
                    rows={3}
                  />
                  {form.formState.errors.excerpt && (
                    <p className="text-sm text-red-500 mt-1">
                      {form.formState.errors.excerpt.message}
                    </p>
                  )}
                </div>

                <div>
                  <Label htmlFor="content">Content</Label>
                  <Textarea
                    id="content"
                    {...form.register("content")}
                    placeholder="Write your post content here..."
                    rows={12}
                  />
                  {form.formState.errors.content && (
                    <p className="text-sm text-red-500 mt-1">
                      {form.formState.errors.content.message}
                    </p>
                  )}
                </div>

                <div>
                  <Label htmlFor="coverImage">Cover Image URL</Label>
                  <Input
                    id="coverImage"
                    {...form.register("coverImage")}
                    placeholder="https://example.com/image.jpg (optional)"
                  />
                </div>

                <div>
                  <Label htmlFor="tags">Tags (comma-separated)</Label>
                  <Input
                    id="tags"
                    placeholder="react, javascript, tutorial"
                    defaultValue={form.getValues("tags")?.join(", ")}
                    onChange={(e) => {
                      const tags = e.target.value.split(",").map(tag => tag.trim()).filter(Boolean);
                      form.setValue("tags", tags);
                    }}
                  />
                </div>

                <div className="flex items-center space-x-2">
                  <Switch
                    id="published"
                    checked={form.watch("published") === "true"}
                    onCheckedChange={(checked) => {
                      form.setValue("published", checked ? "true" : "false");
                      if (checked && !editingPost) {
                        form.setValue("publishedAt", new Date());
                      }
                    }}
                  />
                  <Label htmlFor="published">Publish post</Label>
                </div>

                <div className="flex gap-2 pt-4">
                  <Button type="submit" disabled={createMutation.isPending || updateMutation.isPending}>
                    {createMutation.isPending || updateMutation.isPending ? (
                      <>
                        <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                        Saving...
                      </>
                    ) : editingPost ? (
                      "Update Post"
                    ) : (
                      "Create Post"
                    )}
                  </Button>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => {
                      setDialogOpen(false);
                      setEditingPost(null);
                      form.reset();
                    }}
                  >
                    Cancel
                  </Button>
                </div>
              </form>
            </DialogContent>
          </Dialog>
        </div>

        {isLoading ? (
          <div className="flex justify-center items-center py-20">
            <Loader2 className="h-8 w-8 animate-spin text-primary" />
          </div>
        ) : sortedPosts && sortedPosts.length > 0 ? (
          <div className="grid gap-6">
            {sortedPosts.map((post) => (
              <Card key={post.id}>
                <CardHeader>
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-2">
                        <CardTitle className="text-xl">{post.title}</CardTitle>
                        {post.published === "true" ? (
                          <Badge variant="default">Published</Badge>
                        ) : (
                          <Badge variant="secondary">Draft</Badge>
                        )}
                      </div>
                      <div className="flex items-center gap-2 text-sm text-muted-foreground">
                        <Calendar className="h-4 w-4" />
                        <span>
                          {post.publishedAt
                            ? new Date(post.publishedAt).toLocaleDateString()
                            : `Updated ${new Date(post.updatedAt).toLocaleDateString()}`}
                        </span>
                      </div>
                    </div>
                    <div className="flex gap-2">
                      <Button
                        variant="outline"
                        size="icon"
                        onClick={() => handleEdit(post)}
                      >
                        <Pencil className="h-4 w-4" />
                      </Button>
                      <AlertDialog>
                        <AlertDialogTrigger asChild>
                          <Button variant="outline" size="icon">
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        </AlertDialogTrigger>
                        <AlertDialogContent>
                          <AlertDialogHeader>
                            <AlertDialogTitle>Delete blog post?</AlertDialogTitle>
                            <AlertDialogDescription>
                              This action cannot be undone. This will permanently delete
                              the blog post.
                            </AlertDialogDescription>
                          </AlertDialogHeader>
                          <AlertDialogFooter>
                            <AlertDialogCancel>Cancel</AlertDialogCancel>
                            <AlertDialogAction
                              onClick={() => deleteMutation.mutate(post.id)}
                            >
                              Delete
                            </AlertDialogAction>
                          </AlertDialogFooter>
                        </AlertDialogContent>
                      </AlertDialog>
                    </div>
                  </div>
                </CardHeader>
                <CardContent>
                  <p className="text-muted-foreground mb-4">{post.excerpt}</p>
                  <div className="flex flex-wrap gap-2">
                    {post.tags.map((tag, index) => (
                      <Badge key={index} variant="secondary">
                        {tag}
                      </Badge>
                    ))}
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : (
          <div className="text-center py-20">
            <p className="text-muted-foreground">No blog posts yet. Create your first one!</p>
          </div>
        )}
      </div>
    </div>
  );
}

server/db-storage.ts.content:
import { eq } from 'drizzle-orm';
import { db } from './db';
import {
  projects,
  certificates,
  skills,
  services,
  socialLinks,
  contactMessages,
  users,
  blogPosts,
  aboutContent,
  type Project,
  type InsertProject,
  type Certificate,
  type InsertCertificate,
  type Skill,
  type InsertSkill,
  type Service,
  type InsertService,
  type SocialLink,
  type InsertSocialLink,
  type ContactMessage,
  type InsertContactMessage,
  type User,
  type InsertUser,
  type BlogPost,
  type InsertBlogPost,
  type AboutContent,
  type InsertAboutContent,
} from '@shared/schema';
import session from 'express-session';
import createMemoryStore from 'memorystore';
import type { IStorage } from './storage';

const MemoryStore = createMemoryStore(session);

export class DbStorage implements IStorage {
  public sessionStore: session.Store;

  constructor() {
    this.sessionStore = new MemoryStore({
      checkPeriod: 86400000,
    });
  }

  // Projects
  async getProjects(): Promise<Project[]> {
    const result = await db
      .select()
      .from(projects)
      .orderBy(projects.order);
    return result;
  }

  async getProject(id: string): Promise<Project | undefined> {
    const result = await db
      .select()
      .from(projects)
      .where(eq(projects.id, id))
      .limit(1);
    return result[0];
  }

  async createProject(insertProject: InsertProject): Promise<Project> {
    const result = await db
      .insert(projects)
      .values(insertProject)
      .returning();
    return result[0];
  }

  async updateProject(id: string, insertProject: InsertProject): Promise<Project | undefined> {
    const result = await db
      .update(projects)
      .set(insertProject)
      .where(eq(projects.id, id))
      .returning();
    return result[0];
  }

  async deleteProject(id: string): Promise<boolean> {
    const result = await db
      .delete(projects)
      .where(eq(projects.id, id))
      .returning();
    return result.length > 0;
  }

  // Certificates
  async getCertificates(): Promise<Certificate[]> {
    const result = await db
      .select()
      .from(certificates)
      .orderBy(certificates.createdAt);
    return result;
  }

  async getCertificate(id: string): Promise<Certificate | undefined> {
    const result = await db
      .select()
      .from(certificates)
      .where(eq(certificates.id, id))
      .limit(1);
    return result[0];
  }

  async createCertificate(insertCertificate: InsertCertificate): Promise<Certificate> {
    const result = await db
      .insert(certificates)
      .values(insertCertificate)
      .returning();
    return result[0];
  }

  async updateCertificate(id: string, insertCertificate: InsertCertificate): Promise<Certificate | undefined> {
    const result = await db
      .update(certificates)
      .set(insertCertificate)
      .where(eq(certificates.id, id))
      .returning();
    return result[0];
  }

  async deleteCertificate(id: string): Promise<boolean> {
    const result = await db
      .delete(certificates)
      .where(eq(certificates.id, id))
      .returning();
    return result.length > 0;
  }

  // Skills
  async getSkills(): Promise<Skill[]> {
    const result = await db
      .select()
      .from(skills);
    return result;
  }

  async getSkill(id: string): Promise<Skill | undefined> {
    const result = await db
      .select()
      .from(skills)
      .where(eq(skills.id, id))
      .limit(1);
    return result[0];
  }

  async createSkill(insertSkill: InsertSkill): Promise<Skill> {
    const result = await db
      .insert(skills)
      .values(insertSkill)
      .returning();
    return result[0];
  }

  async updateSkill(id: string, insertSkill: InsertSkill): Promise<Skill | undefined> {
    const result = await db
      .update(skills)
      .set(insertSkill)
      .where(eq(skills.id, id))
      .returning();
    return result[0];
  }

  async deleteSkill(id: string): Promise<boolean> {
    const result = await db
      .delete(skills)
      .where(eq(skills.id, id))
      .returning();
    return result.length > 0;
  }

  // Services
  async getServices(): Promise<Service[]> {
    const result = await db
      .select()
      .from(services);
    return result;
  }

  async getService(id: string): Promise<Service | undefined> {
    const result = await db
      .select()
      .from(services)
      .where(eq(services.id, id))
      .limit(1);
    return result[0];
  }

  async createService(insertService: InsertService): Promise<Service> {
    const result = await db
      .insert(services)
      .values(insertService)
      .returning();
    return result[0];
  }

  async updateService(id: string, insertService: InsertService): Promise<Service | undefined> {
    const result = await db
      .update(services)
      .set(insertService)
      .where(eq(services.id, id))
      .returning();
    return result[0];
  }

  async deleteService(id: string): Promise<boolean> {
    const result = await db
      .delete(services)
      .where(eq(services.id, id))
      .returning();
    return result.length > 0;
  }

  // Social Links
  async getSocialLinks(): Promise<SocialLink[]> {
    const result = await db
      .select()
      .from(socialLinks)
      .orderBy(socialLinks.order);
    return result;
  }

  async getSocialLink(id: string): Promise<SocialLink | undefined> {
    const result = await db
      .select()
      .from(socialLinks)
      .where(eq(socialLinks.id, id))
      .limit(1);
    return result[0];
  }

  async createSocialLink(insertLink: InsertSocialLink): Promise<SocialLink> {
    const result = await db
      .insert(socialLinks)
      .values(insertLink)
      .returning();
    return result[0];
  }

  async updateSocialLink(id: string, insertLink: InsertSocialLink): Promise<SocialLink | undefined> {
    const result = await db
      .update(socialLinks)
      .set(insertLink)
      .where(eq(socialLinks.id, id))
      .returning();
    return result[0];
  }

  async deleteSocialLink(id: string): Promise<boolean> {
    const result = await db
      .delete(socialLinks)
      .where(eq(socialLinks.id, id))
      .returning();
    return result.length > 0;
  }

  // Contact Messages
  async createContactMessage(insertMessage: InsertContactMessage): Promise<ContactMessage> {
    const result = await db
      .insert(contactMessages)
      .values(insertMessage)
      .returning();
    return result[0];
  }

  async getContactMessages(): Promise<ContactMessage[]> {
    const result = await db
      .select()
      .from(contactMessages)
      .orderBy(contactMessages.createdAt);
    return result;
  }

  // Users
  async getUser(id: number): Promise<User | undefined> {
    const result = await db
      .select()
      .from(users)
      .where(eq(users.id, id))
      .limit(1);
    return result[0];
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const result = await db
      .select()
      .from(users)
      .where(eq(users.username, username))
      .limit(1);
    return result[0];
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const result = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return result[0];
  }

  // Blog Posts
  async getBlogPosts(publishedOnly: boolean = false): Promise<BlogPost[]> {
    if (publishedOnly) {
      const result = await db
        .select()
        .from(blogPosts)
        .where(eq(blogPosts.published, 'true'))
        .orderBy(blogPosts.publishedAt);
      return result;
    }
    const result = await db
      .select()
      .from(blogPosts)
      .orderBy(blogPosts.updatedAt);
    return result;
  }

  async getBlogPost(id: string): Promise<BlogPost | undefined> {
    const result = await db
      .select()
      .from(blogPosts)
      .where(eq(blogPosts.id, id))
      .limit(1);
    return result[0];
  }

  async getBlogPostBySlug(slug: string): Promise<BlogPost | undefined> {
    const result = await db
      .select()
      .from(blogPosts)
      .where(eq(blogPosts.slug, slug))
      .limit(1);
    return result[0];
  }

  async createBlogPost(insertPost: InsertBlogPost): Promise<BlogPost> {
    const result = await db
      .insert(blogPosts)
      .values(insertPost)
      .returning();
    return result[0];
  }

  async updateBlogPost(id: string, insertPost: InsertBlogPost): Promise<BlogPost | undefined> {
    const result = await db
      .update(blogPosts)
      .set({
        ...insertPost,
        updatedAt: new Date(),
      })
      .where(eq(blogPosts.id, id))
      .returning();
    return result[0];
  }

  async deleteBlogPost(id: string): Promise<boolean> {
    const result = await db
      .delete(blogPosts)
      .where(eq(blogPosts.id, id))
      .returning();
    return result.length > 0;
  }

  // About Content
  async getAboutContent(): Promise<AboutContent | undefined> {
    const result = await db
      .select()
      .from(aboutContent)
      .limit(1);
    return result[0];
  }

  async createOrUpdateAboutContent(insertContent: InsertAboutContent): Promise<AboutContent> {
    const existing = await this.getAboutContent();

    if (existing) {
      const result = await db
        .update(aboutContent)
        .set({
          ...insertContent,
          updatedAt: new Date(),
        })
        .where(eq(aboutContent.id, existing.id))
        .returning();
      return result[0];
    } else {
      const result = await db
        .insert(aboutContent)
        .values(insertContent)
        .returning();
      return result[0];
    }
  }
}

